<!DOCTYPE html><html lang="eng"><head><title>Bar Graph Presentation</title><script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>    <script type="text/javascript">		<?php	include_once 'db_con.php';	$result = mysqli_query($db_con, "select patterns.name as 'filename', count(*) as 'totaldownloads' from downloads									left outer join pattern_files on downloads.pattern_file_id =pattern_files.id 									left outer join patterns on patterns.id = pattern_files.pattern_id									group by pattern_file_id ");	$graph_data = "";	while($row = mysqli_fetch_array($result)){		$total_downloads = $row['totaldownloads'];		$files_name = $row['filename'];						$graph_data .= "['$files_name', $total_downloads],";						}		?> google.charts.load('current', {'packages':['bar']});      google.charts.setOnLoadCallback(drawChart);      function drawChart() {        var data = google.visualization.arrayToDataTable([          ['Name', 'Downloads'],         <?php echo $graph_data; ?>        ]);        var options = {          chart: {            title: 'Company Performance',            subtitle: 'Filename and Total Downloads',          },          bars: 'horizontal' // Required for Material Bar Charts.        };        var chart = new google.charts.Bar(document.getElementById('barchart_material'));        chart.draw(data, options);      }    </script></head><div id="barchart_material" style="width: auto; height: 700px;"></div></html>