<!DOCTYPE html><html lang="eng"><head><title>Bar Graph Presentation</title> <script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>   <?php	include_once 'db_con.php';	$result = mysqli_query($db_con, "select patterns.name as file_name, reviews.question_1 as a, reviews.question_2 as b, reviews.question_3 as c, reviews.question_4 as d, reviews.question_5 as e from reviews									join pattern_files on pattern_files.id = reviews.pattern_file_id									join patterns on patterns.id = pattern_files.pattern_id									group by pattern_file_id  ");	$graph_data = "";	while($row = mysqli_fetch_array($result)){		$files_name = $row['file_name'];		$reviews_a = $row['a'];		$reviews_b = $row['b'];		$reviews_c = $row['c'];		$reviews_d = $row['d'];		$reviews_e = $row['e'];						$graph_data .= "['$files_name', $reviews_a, $reviews_b, $reviews_c, $reviews_d, $reviews_e],";						}		?> 	<script type="text/javascript">    google.load('visualization', '1.1', {'packages': ['bar']});	google.setOnLoadCallback(drawStuff);function drawStuff() {    var data = new google.visualization.DataTable();    data.addColumn('string', 'Topping');    data.addColumn('number', 'Nescafe Instant');    data.addColumn('number', 'Folgers Instant');    data.addColumn('number', 'Nescafe Beans');    data.addColumn('number', 'Folgers Beans');    data.addRows([        ['2001', 321, 621, 816, 319],        ['2002', 163, 231, 539, 594],        ['2003', 125, 819, 123, 578],        ['2004', 197, 536, 613, 298]    ]);    // Set chart options    var options = {        isStacked: true,        width: 800,        height: 600,        chart: {            title: 'Year-by-year coffee consumption',            subtitle: 'This data is not real'        },        vAxis: {            viewWindow: {                min: 0,                max: 1000            }        },        series: {            2: {                targetAxisIndex: 1            },            3: {                targetAxisIndex: 1            }        }    };    // Instantiate and draw our chart, passing in some options.    var chart = new google.charts.Bar(document.getElementById('chart_div'));    chart.draw(data, google.charts.Bar.convertOptions(options));};  </script>  </head><div id="chart_div"></div></html>