<!DOCTYPE html><html lang="eng"><head><title>Bar Graph Presentation</title><script src="https://code.jquery.com/jquery-1.9.1.js"></script><script src="https://code.highcharts.com/highcharts.js"></script><script src="https://code.highcharts.com/modules/exporting.js"></script><script type="text/javascript"><?php	include_once 'db_con.php';	$result = mysqli_query($db_con, "select patterns.name as 'filename', count(*) as 'totaldownloads' from downloads									left outer join pattern_files on downloads.pattern_file_id =pattern_files.id 									left outer join patterns on patterns.id = pattern_files.pattern_id									group by pattern_file_id ");	$graph_data = "";		while($row = mysqli_fetch_array($result)){		$total_downloads = $row['totaldownloads'];		$files_name = $row['filename'];						$graph_data .= "{'name':'$files_name', 'data':[$total_downloads]},";								}		?>$(function () {    $('#container').highcharts({        chart: {        type: 'bar',        },        title: {            text: 'Historic World Population by Region'        },        subtitle: {            text: 'Source: <a href="https://en.wikipedia.org/wiki/World_population">Wikipedia.org</a>'        },        xAxis: {			categories: [''],			title:{				text: 'Patterns Name'			}        },        yAxis: {            min: 0,            title: {                text: 'Downloads',                align: 'high'            },            labels: {                overflow: 'justify'            }        },        tooltip: {			 pointFormat: '<p><strong>{series.name}</strong></p><br><p><span style="color:#1E9E6B;">Ratings:</span><?php echo '2'?><p><br><p>Comment: Nice product</p><br><p>Comment: Nice product</p><br><p>Comment: Nice product</p><br>',            valueSuffix: ' downloads'        },        plotOptions: {            bar: {                dataLabels: {                    enabled: true                }            },			series:{				pointWidth: 12,				pointPadding: 0,                groupPadding: .0			}        },        legend: {           enabled:false        },        credits: {            enabled: false        },        series: [		<?php echo $graph_data; ?>					]    });});</script>  </head>  <body><div id="container" style="width:100; height:900px;"></div></body></html>