<!DOCTYPE html><html lang="eng"><head><title>Tabular Prsentation</title><script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>   <?php	include_once 'db_con.php';	$result = mysqli_query($db_con, "select patterns.name as 'filename', count(*) as 'totaldownloads' from downloads									inner join pattern_files on downloads.pattern_file_id =pattern_files.id 									inner join patterns on patterns.id = pattern_files.pattern_id									group by pattern_file_id  ");	$graph_data = "";	while($row = mysqli_fetch_array($result)){		$total_downloads = $row['totaldownloads'];		$files_name = $row['filename'];						$graph_data .= "['$files_name', $total_downloads],";						}		?>		<script type="text/javascript">      google.charts.load('current', {'packages':['table']});      google.charts.setOnLoadCallback(drawTable);      function drawTable() {        var data = new google.visualization.DataTable();        data.addColumn('string', 'File Name');        data.addColumn('number', 'Total Downloads');               data.addRows([          <?php echo $graph_data;?>                ]);        var table = new google.visualization.Table(document.getElementById('table_div'));        table.draw(data, {showRowNumber: true, width: '100%', height: 'auto'});      }    </script></head><div id="table_div"></div></html>