<!DOCTYPE html><html lang="eng"><head><title>Digital Design</title><script src="https://code.jquery.com/jquery-1.9.1.js"></script><script src="https://code.highcharts.com/highcharts.js"></script><script src="https://code.highcharts.com/modules/exporting.js"></script><script type="text/javascript"><?php	include_once 'db_con.php';	/*$result = mysqli_query($db_con, "select patterns.name as 'filename', count(*) as 'totaldownloads' from downloads									left outer join pattern_files on downloads.pattern_file_id =pattern_files.id 									left outer join patterns on patterns.id = pattern_files.pattern_id									group by pattern_file_id ");	*/			$result = mysqli_query($db_con, "select pattern_file_id as id, patterns.name as Name,  ROUND(AVG(question_1)) as Rating,  group_concat(`question_2`) as Problems,  group_concat(`question_5`) as 'Comments' 		FROM 	reviews 		left outer join pattern_files on reviews.pattern_file_id =pattern_files.id 		left outer join patterns on patterns.id = pattern_files.pattern_id		where question_1 >0 and question_5 !=''		group by reviews.pattern_file_id"); 	$graph_data = "";	$patterns_name = "";		while($row = mysqli_fetch_array($result)){		$rating = $row['Rating'];		//$files_name = $row['filename'];		$file_id = $row['id'];				$problems= $row['Problems'];				$comments= $row['Comments'];								$graph_data .= "{y:$rating,comments:'$comments',problems:'$problems'},";		$patterns_name .= "'$file_id',";						}		?>$(function () {    var chart = new Highcharts.Chart({        chart: {            renderTo: 'container',            type: 'column'        },        xAxis: {            categories: [<?php echo $patterns_name; ?>]        },        series: [{            data: [<?php echo $graph_data; ?>]        },],        tooltip: {            formatter: function() {return ' ' +                'comments: ' + this.point.comments + '<br />' +                'Problems: ' + this.point.problems + '<br />' ;                            }        }    });});</script>  </head>  <body>  <p><?php echo $patterns_name; ?></p><div id="container" style="width:100; height:900px;"></div></body></html>